    private void btncalcularcuboActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btncalcularcuboActionPerformed
        double diagonal=Double.parseDouble(txtdiagonalcubo.getText());
        txtresultadoarista.setText(String.valueOf(diagonal/1.414213562));
       double arista=Double.parseDouble(txtresultadoarista.getText());
        txtresultadocubo.setText(String.valueOf(arista*arista*arista));
    }//GEN-LAST:event_btncalcularcuboActionPerformed

